import { useState } from "react";

export const DOTS = "...";

function usePagination(props) {

  console.log(props);
  let temp = [];

  let maxPage = Math.ceil(props.totalCount/props.pageSize);

  let min = (props.currentPage == maxPage && maxPage != 1) ? maxPage -2 : Math.max(1, props.currentPage-1);
  let max = Math.min(min+2, maxPage);
  for(var i = min; i <= max; i++) temp.push(i)

  if(temp[0] > 1){
    temp.unshift(1, DOTS);
  }
  if(temp.at(-1) < maxPage){
    temp.push(DOTS, maxPage);
  }

  console.log(props.currentPage);

  return temp;
}

export default usePagination;
