import BlogPost from "./BlogPost";
import Pagination from "./Pagination";
import React, {useState} from "react";
import blogs from "../data/blogs.json";

const PAGE_SIZES = [15, 25, 50, 100];

function BlogList() {
  const [currentPage, setCurrentPage] = useState(1);
  const [currentPaginationData, setCurrentPaginationData] = useState(blogs.posts.slice(0, 15));
  const [pageSize, setPageSize] = useState(100);

  const maxPage = Math.ceil(blogs.posts.length/pageSize)

  const handlePageChange = (step) => {
    setCurrentPage(prev => {
      let res = prev + step;
      if( (res) < 1) return 1
      else if( (res) > maxPage) return maxPage
      else return res
    });
  }

  const handlePageSize = (size) => {
    setPageSize(size);
  }

  const updateRowsPerPage = (pageSize) => {

  };
  const updatePage = (page) => {
    setCurrentPage(page);
  };

  return (
    <div>
      <Pagination
        currentPage={currentPage}
        handlePageChange = {handlePageChange}

        totalCount={blogs.posts.length}

        pageSize={pageSize}
        handlePageSize = {handlePageSize}

        pageSizeOptions={PAGE_SIZES}
        onPageChange={updatePage}
        onPageSizeOptionChange={updateRowsPerPage}
      />
      <ul
        // Do not modify the aria-label below, it is used for Hatchways automation.
        aria-label="blog list"
      >
        {currentPaginationData.map((blog) => (
          <BlogPost
            key={blog.id}
            author={blog.author}
            title={blog.title}
            excerpt={blog.excerpt}
            featureImage={blog.image}
          />
        ))}
      </ul>
    </div>
  );
}

export default BlogList;
