import { useState } from "react";

export const DOTS = "...";

function usePagination({currentPage, totalCount, pageSize}) {

  const [activePage, setActivePage] = useState(currentPage);

  let temp = [];

  let range = Math.ceil(totalCount/pageSize);

  let min = activePage == range ? range -2 : Math.max(1, activePage-1);
  let max = Math.min(min+2, range);
  for(var i = min; i <= max; i++) temp.push(i)

  if(temp[0] > 1){
    temp.unshift(1, DOTS);
  }
  if(temp.at(-1) < range){
    temp.push(DOTS, range);
  }

  const handleActivePage = (val) => {
    setActivePage(prev => {
      if( (prev + val) < 1) return 1
      else if( (prev + val) > range) return range
      else return prev + val 
    });
  }

  console.log(activePage);

  return [temp, handleActivePage];
}

export default usePagination;
