import { useState } from "react";

export const DOTS = "...";

function usePagination({currentPage, totalCount, pageSize}) {
  let range;
  let temp = [];
  if(currentPage == 1){
    range = Math.ceil(totalCount/pageSize);
    temp = [currentPage-1, currentPage, currentPage+1]

    if(temp[0] > 1){
      temp.unshift(1, DOTS);
    }
    if(temp[-1] < range){
      temp.push(DOTS);
    }
  }
  temp.push(range);
  return temp;
}

export default usePagination;
