import { useState } from "react";

export const DOTS = "...";

function usePagination({currentPage, totalCount, pageSize}) {
  let range;
  let temp = [];

  range = Math.ceil(totalCount/pageSize);
  temp = [currentPage-1, currentPage, currentPage+1]
  let min = Math.max(1, currentPage-1);
  let max = Math.min(min+2, range);
  if(temp[0] > 1){
    temp.unshift(1, DOTS);
  }
  if(temp[-1] < range){
    temp.push(DOTS);
  }
  temp.push(range);
  return [1, 2, 3, DOTS, 5];
}

export default usePagination;
