import { useState } from "react";

export const DOTS = "...";

function usePagination({currentPage, totalCount, pageSize}) {
  const [activePage, setActivePage] = useState(currentPage);
  let range;
  let temp = [];
  console.log(currentPage);
  range = Math.ceil(totalCount/pageSize);
  let min = Math.max(1, currentPage-1);
  let max = Math.min(min+2, range);
  for(var i = min; i <= max; i++) temp.push(i)
  if(temp[0] > 1){
    temp.unshift(1, DOTS);
  }
  if(temp.at(-1) < range){
    temp.push(DOTS);
  }
  temp.push(range);

  const handleActivePage = () => {
    setActivePage(prev => prev + 1);
  }

  return [temp, handleActivePage];
}

export default usePagination;
